package practice;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=3;
		int c=7;
	    System.out.println(a&b);
	    System.out.println(a|b);
	    System.out.println(a^b);
	    System.out.println(a>b||b>c);
	    System.out.println(a>b&&c>b);
	    System.out.println(a>c?a:c);

	}

}
